import React, { useState, use } from "react";
import { useNavigate } from "react-router-dom";
import "./CreateAccount.css"; // Import the updated CSS
// import { registerUser } from "../api"; //added by s

function CreateAccount() {
    const navigate = useNavigate();
    const [isArabic, setIsArabic] = useState(false); // Language state
    const [formData, setFormData] = useState({
        username: "",
        email: "",
        password: "",
        confirmPassword: "",
    });

    const [errors, setErrors] = useState({
        // username: "",
        // email: "",
        // password: "",
        // confirmPassword: "",
    });

    // Toggle Language
    const toggleLanguage = () => {
        setIsArabic((prev) => !prev);
    };

    // Handle input change
    const handleChange = (e) => {
        // const { name, value } = e.target;
        // setFormData({ ...formData, [name]: value });
        setFormData({ ...formData, [e.target.name]: e.target.value }); //new added by s
    };

    // Validate and submit form
    const handleSubmit = async (e) => {
        e.preventDefault();
        //let newErrors = { username: "", email: "", password: "", confirmPassword: "" };
        const newErrors = {}; // new added by s

        if (!formData.username) {
            newErrors.username = isArabic ? "ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ•ÿØÿÆÿßŸÑ ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ" : "Please enter a username";
        }
        if (!formData.email) {
            newErrors.email = isArabic ? "ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ•ÿØÿÆÿßŸÑ ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä" : "Please enter an email";
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = isArabic ? "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠" : "Invalid email format";
        }
        if (!formData.password) {
            newErrors.password = isArabic ? "ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ•ÿØÿÆÿßŸÑ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±" : "Please enter a password";
        }
        if (formData.password !== formData.confirmPassword) {
            newErrors.confirmPassword = isArabic ? "ŸÉŸÑŸÖÿßÿ™ ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ŸÖÿ™ÿ∑ÿßÿ®ŸÇÿ©" : "Passwords do not match";
        }

        setErrors(newErrors);

        // If no errors, navigate to the avatar selection page
        //if (!newErrors.username && !newErrors.email && !newErrors.password && !newErrors.confirmPassword) {
            // old added by s from here..
            // try {
            //     // added by s to confirm the frontend is making the request bc db not working
            //     console.log("Sending registration data to backend:", {
            //         username: formData.username,
            //         email: formData.email,
            //         password: formData.password,
            //     }); // ‚úÖ Call backend API

            //     const result = await registerUser({
            //         username: formData.username,
            //         email: formData.email,
            //         password: formData.password,
            //     });

            //     if (result.error) {
            //         alert(result.error); // ‚úÖ Show error if registration fails
            //         return;
            //     }

            //     // localStorage.setItem("user", JSON.stringify(result.user)); // ‚úÖ Store user
            // } catch (error) {
            //     console.error("Registration failed:", error);
            //     alert("Registration failed. Please try again.");
            // }
        // till here    


        //     navigate("/avatar");
        // }

        //new added by s from here...
        if (Object.keys(newErrors).length > 0) return;

        try {
            const response = await fetch("http://localhost:8000/auth/register", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                username: formData.username,
                email: formData.email,
                password: formData.password,
                language: isArabic ? "ar" : "en"
            })
        });

        if (!response.ok) throw new Error("Registration failed");
        const result = await response.json();
        setUserId(result.user.id);  // ‚úÖ new line here
        navigate(`/avatar/${result.user.id}`);
    }   catch (error) {
        console.error("Registration error:", error);
        alert("Registration failed.");
    }
  };
//till here (new)

return (
    <div className={`create-account-container ${isArabic ? "rtl" : ""}`}>

        {/* Header */}
        <header className="create-account-header">
            <h1 className="create-account-title1">
                {isArabic ? "ÿπŸéÿ®ŸêŸëÿ±Ÿí" : "Abber"}
            </h1>

            {/* Language switch button */}
            <button className="create-acc-language-switch" onClick={toggleLanguage}>
                {isArabic ? "English" : "ÿßŸÑÿπÿ±ÿ®Ÿäÿ©"}
            </button>
        </header>       

        {/* Create Account Form Title */}
        <h1 className="create-account-title">
            {isArabic 
                ? "üòä ÿ£ŸáŸÑÿßŸã ÿ®ŸÉ! ŸÑŸÜŸÜÿ¥ÿ¶ ÿ≠ÿ≥ÿßÿ®ŸÉ ŸÖÿπŸãÿß "  
                : "Let's Create Your Account Together üòä"}
        </h1>

        {/* Wrapper for form and buttons */}
        <div className="form-wrapper">
            {/* Form */}
            <form className={`create-account-form ${isArabic ? "rtl" : ""}`} onSubmit={handleSubmit}>
                {/* Username */}
                <label className="form-label">
                    {isArabic ? "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ" : "Username"}
                    <input
                        type="text"
                        name="username"
                        value={formData.username}
                        onChange={handleChange}
                    />
                    {errors.username && <p className="error">{errors.username}</p>}
                </label>

                {/* Email */}
                <label className="form-label">
                    {isArabic ? "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä" : "Email"}
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                    />
                    {errors.email && <p className="error">{errors.email}</p>}
                </label>

                {/* Password */}
                <label className="form-label">
                    {isArabic ? "ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±" : "Password"}
                    <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                    />
                    {errors.password && <p className="error">{errors.password}</p>}
                </label>

                {/* Confirm Password */}
                <label className="form-label">
                    {isArabic ? "ÿ™ÿ£ŸÉŸäÿØ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±" : "Confirm Password"}
                    <input
                        type="password"
                        name="confirmPassword"
                        value={formData.confirmPassword}
                        onChange={handleChange}
                    />
                    {errors.confirmPassword && <p className="error">{errors.confirmPassword}</p>}
                </label>

                {/* Submit Button */}
                <button type="submit" className="create-account-button">
                    {isArabic ? "ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ≠ÿ≥ÿßÿ®" : "Create Account"}
                </button>
            </form>

            {/* Already have an account? */}
            <p className="already-account-text" onClick={() => navigate("/login")}>
                {isArabic ? "ŸáŸÑ ŸÑÿØŸäŸÉ ÿ≠ÿ≥ÿßÿ® ÿ®ÿßŸÑŸÅÿπŸÑÿü ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ" : "Already have an account? Login"}
            </p>

            {/* Back to Home Button */}
            <button className="home-button primary" onClick={() => navigate("/")}>
                {isArabic ? "ÿßŸÑÿπŸàÿØÿ© ÿ•ŸÑŸâ ÿßŸÑÿµŸÅÿ≠ÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©" : "Back to Home"}
            </button>
        </div>
    </div>
);
}


export default CreateAccount;
