import React, { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import "./LoginPage.css"; // Import styles
// import { loginUser } from "../api"; // added by s

export default function LoginPage() {
  const navigate = useNavigate();
  const { language, toggleLanguage } = useContext(AuthContext);

  const [formData, setFormData] = useState({
    username: "",
    password: "",
  });
  const [error, setError] = useState("");//new added by s


  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {    //git from here
    e.preventDefault();

    // if (formData.username && formData.password) {
      try {
        // const result = await loginUser(formData); // Call backend API
        // new added by s from here...
        const response = await fetch("http://localhost:8000/auth/login", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(formData),
        });
        // till here (new)

        // if (result.error) {
        //   alert(result.error);
        //   return;
        // }

        // new added by s from here ...
        if (!response.ok) {
          setError("Invalid credentials");
          return;
        }

        const data = await response.json();
        navigate(`/profile/${data.user.id}`);
      } 
      catch (err) {
        console.error("Login error:", err);
        setError("Login failed");
      }
    };


          //till here (new)

        // ‚úÖ Store the entire user object and userId
  //       localStorage.setItem("user", JSON.stringify(result.user));

  //       // ‚úÖ Now navigate to profile or main page
  //       console.log("Logged in successfully:", result.user);
  //       navigate("/main-page");
  //     } catch (error) {
  //       console.error("Login failed:", error);
  //       alert("Login failed. Please try again.");
  //     }
  //   } else {
  //     alert("Please fill in all fields!");
  //   }
  // }; // git here

  // üîπ Form Fields Array (Reverse Order in Arabic)
  const formFields = [
    { label: "Username:", labelAr: "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:", name: "username", type: "text" },
    { label: "Password:", labelAr: "ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±:", name: "password", type: "password" },
  ];

  // üîπ Reverse order for Arabic
  const orderedFields = language === "en" ? formFields : [...formFields].reverse();

  return (
    <div className="login-container">
      <header className={`login-header ${language === "en" ? "rtl" : ""}`}>
        <h1 className="login-title1">
          {language === "en" ? "Abber" : "ÿπŸéÿ®ŸêŸëÿ±Ÿí"}
        </h1>

        {/* üîπ Language Switch Button */}
        <button className="login-language-switch" onClick={toggleLanguage}>
          {language === "en" ? "ÿßŸÑÿπÿ±ÿ®Ÿäÿ©" : "English"}
        </button>
      </header>

      <h1 className="login-title">
        {language === "en" ? "Welcome Back! Login üòä" : " üòä ŸÖÿ±ÿ≠ÿ®Ÿãÿß ÿ®ÿπŸàÿØÿ™ŸÉ! ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ "}
      </h1>

      <form
        className="login-form"
        onSubmit={handleSubmit}
        dir={language === "en" ? "ltr" : "rtl"} // Adjust direction
      >
        {orderedFields.map((field) => (
          <label key={field.name} className="form-label">
            {language === "en" ? field.label : field.labelAr}
            <input
              type={field.type}
              name={field.name}
              value={formData[field.name]}
              onChange={handleChange}
              required
            />
          </label>
        ))}

        <button type="submit" className="login-button">
          {language === "en" ? "Login" : "ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ"}
        </button>
      </form>

      {/* üîπ "Don't Have an Account?" */}
      <p className="create-account-text" onClick={() => navigate("/create-account")}>
        {language === "en" ? "Don't have an account? Create one" : "ŸÑŸäÿ≥ ŸÑÿØŸäŸÉ ÿ≠ÿ≥ÿßÿ®ÿü ŸÇŸÖ ÿ®ÿ•ŸÜÿ¥ÿßÿ° Ÿàÿßÿ≠ÿØ"}
      </p>

      {/* üîπ Back to Home Button */}
      <button className="home-button primary" onClick={() => navigate("/")}>
        {language === "en" ? "Back to Home" : "ÿßŸÑÿπŸàÿØÿ© ÿ•ŸÑŸâ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©"}
      </button>
    </div>
  );
}
