import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
// import { getUserProfile, updateUserProfile } from "../api";
import * as Avatars from '@dicebear/avataaars';
import { createAvatar } from '@dicebear/core';
import "./Profile.css";

export default function Profile() {
  const { language, toggleLanguage } = useContext(AuthContext);
  const navigate = useNavigate();
  const { userId } = useParams();

  const [user, setUser] = useState(null);
  const [error, setError] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [newUsername, setNewUsername] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [selectedAvatar, setSelectedAvatar] = useState("");
  const [avatars, setAvatars] = useState([]);

  // Safely parse the user data from localStorage
  // let userData = null;
  // const userJson = localStorage.getItem("user");
  
  // if (userJson) {
  //   try {
  //     userData = JSON.parse(userJson); // Only try to parse if it's not null
  //   } catch (err) {
  //     console.error("Error parsing user data from localStorage:", err);
  //   }
  // }

  // const userId = userData ? userData.id : null;

  // useEffect(() => {
  //   const fetchUserProfile = async () => {
      
  //     if (!userId) {
  //       setError("No user ID found. Please log in again.");
  //       return;
  //     }

  //     try {
  //       const data = await getUserProfile(userId);
  //       setUser(data);
  //       setNewUsername(data.username);
  //       setNewEmail(data.email);
  //     } catch (err) {
  //       setError("Error fetching profile data");
  //       console.error(err);
  //     }
  //   };

  //   if (userId) {
  //     fetchUserProfile();
  //     const avatar = localStorage.getItem(`avatar_${userId}`); //added
  //     if (avatar) setSelectedAvatar(avatar); //added
  //   }
    
  //   generateAvatars(); // Generate avatars when the component loads
  // }, [userId]);

  // new added by s from here...
  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const response = await fetch(`http://localhost:8000/user/${userId}`);
        if (!response.ok) throw new Error("User not found");
        const data = await response.json();
        setUser(data);
        setNewUsername(data.username);
        setNewEmail(data.email);
        setSelectedAvatar(data.avatar || "");
      } catch (err) {
        setError("Error fetching profile data");
      }
    };

    fetchUserProfile();
    generateAvatars();
  }, [userId]);
  //..till here (new)

  const generateAvatars = () => {
    const newAvatars = [];
    for (let i = 0; i < 6; i++) {
      const svg = createAvatar(Avatars, {
        seed: Math.random().toString(36).substring(7),
        backgroundColor: '#ffffff',
      }).toString();
      newAvatars.push(svg);
    }
    setAvatars(newAvatars);
  };

  const handleEditClick = () => {
    setIsEditing(!isEditing);
  };

  const handleSaveChanges = async () => {
    const updatedUser = {
      ...user,
      username: newUsername,
      email: newEmail,
      avatar: selectedAvatar,  // Save selected avatar
    };

    // try {
    //   await updateUserProfile(userId, updatedUser); // Call API to update profile
    //   setUser(updatedUser);
    //   localStorage.setItem("user", JSON.stringify(updatedUser)); // Save user data with avatar
    //   localStorage.setItem("selectedAvatar", selectedAvatar); // Save selected avatar to localStorage
    //   setIsEditing(false);
    // } catch (err) {
    //   setError("Error saving changes.");
    //   console.error(err);
    // }

    // new added by  s from here ...
    try {
      const response = await fetch(`http://localhost:8000/user/${userId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(updatedUser),
      });

      if (!response.ok) throw new Error("Failed to update");
      setUser({ ...user, ...updatedUser });
      setIsEditing(false);
    } catch (err) {
      setError("Error saving changes.");
    }
    //till here (new)
  };

  return (
    <div className="profile-container">
      <header className={`login-header ${language === "en" ? "rtl" : ""}`}>
        <h1 className="login-title1">{language === "en" ? "Abber" : "ÿπŸéÿ®ŸêŸëÿ±Ÿí"}</h1>
        <button className="login-language-switch" onClick={toggleLanguage}>
          {language === "en" ? "ÿßŸÑÿπÿ±ÿ®Ÿäÿ©" : "English"}
        </button>
      </header>

      <h1 className="login-title">
        {language === "en" ? "Welcome to Your Profile üòä" : " üòä ŸÖÿ±ÿ≠ÿ®Ÿãÿß ÿ®ŸÉ ŸÅŸä ŸÖŸÑŸÅŸÉ ÿßŸÑÿ¥ÿÆÿµŸä"}
      </h1>

      {error && <div>{error}</div>}
      {!user ? (
        <div>Loading...</div>
      ) : (
        <div className="profile-details">
          {selectedAvatar && (
            <div className="profile-avatar-container">
              <div
                className="profile-avatar"
                dangerouslySetInnerHTML={{ __html: selectedAvatar }}
                style={{ width: '100px', height: '100px' }}
              />
            </div>
          )}
          <p><strong>{language === "en" ? "Username:" : "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:"}</strong> {user.username}</p>
          <p><strong>{language === "en" ? "Email:" : "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä:"}</strong> {user.email}</p>
          <p><strong>{language === "en" ? "Language:" : "ÿßŸÑŸÑÿ∫ÿ©:"}</strong> {user.language}</p>
        </div>
      )}

      <button className="edit-button" onClick={handleEditClick}>
        {language === "en" ? "Edit Profile" : "ÿ™ÿπÿØŸäŸÑ ÿßŸÑŸÖŸÑŸÅ ÿßŸÑÿ¥ÿÆÿµŸä"}
      </button>

      {isEditing && (
        <div className="edit-profile-container">
          <div className="input-group">
            <label>{language === "en" ? "Username" : "ÿßÿ≥ŸÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ"}</label>
            <input
              type="text"
              value={newUsername}
              onChange={(e) => setNewUsername(e.target.value)}
            />
          </div>
          <div className="input-group">
            <label>{language === "en" ? "Email" : "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä"}</label>
            <input
              type="email"
              value={newEmail}
              onChange={(e) => setNewEmail(e.target.value)}
            />
          </div>

          {/* Avatar Selection */}
          <div className="avatar-selection-container">
            {avatars.map((avatar, index) => (
              <div
                key={index}
                className={`avatar-item ${selectedAvatar === avatar ? 'selected' : ''}`}
                onClick={() => setSelectedAvatar(avatar)}
                dangerouslySetInnerHTML={{ __html: avatar }}
              />
            ))}
          </div>

          <button
            className="save-button"
            onClick={handleSaveChanges}
          >
            {language === "en" ? "Save Changes" : "ÿ≠ŸÅÿ∏ ÿßŸÑÿ™ÿ∫ŸäŸäÿ±ÿßÿ™"}
          </button>
        </div>
      )}

      <button className="home-button primary" onClick={() => navigate("/main-page")}>
        {language === "en" ? "Back to Home" : "ÿßŸÑÿπŸàÿØÿ© ÿ•ŸÑŸâ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©"}
      </button>
    </div>
  );
}
