import React, { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import "./NextPage.css";

export default function NextPage() {
    const [selectedAvatar, setSelectedAvatar] = useState(null); // Avatar SVG
    const navigate = useNavigate();
    const { language, toggleLanguage } = useContext(AuthContext); // Language state from context

    useEffect(() => {
        // ‚úÖ Loads avatar from localStorage (used temporarily between pages)
        const avatar = localStorage.getItem('selectedAvatar');
        if (avatar) setSelectedAvatar(avatar);
    }, []);

    return (
        <div className="next-page-container">
            {/* Header with language toggle */}
            <header className={`login-header ${language === "en" ? "rtl" : ""}`}>
                <h1 className="login-title1">{language === "en" ? "Abber" : "ÿπŸéÿ®ŸêŸëÿ±Ÿí"}</h1>
                <button className="login-language-switch" onClick={toggleLanguage}>
                    {language === "en" ? "ÿßŸÑÿπÿ±ÿ®Ÿäÿ©" : "English"}
                </button>
            </header>

            {/* Main message */}
            <h1 className="page-title">
                {selectedAvatar
                    ? (language === "en" ? "Your Selected Avatar is" : "ÿßŸÑÿµŸàÿ±ÿ© ÿßŸÑÿ±ŸÖÿ≤Ÿäÿ© ÿßŸÑÿ™Ÿä ÿßÿÆÿ™ÿ±ÿ™Ÿáÿß ŸáŸä")
                    : (language === "en" ? "Oops! No Avatar Selected!" : "ÿπÿ∞ÿ±Ÿãÿß! ŸÑŸÖ Ÿäÿ™ŸÖ ÿßÿÆÿ™Ÿäÿßÿ± ÿµŸàÿ±ÿ© ÿ±ŸÖÿ≤Ÿäÿ©!")}
            </h1>

            {/* Avatar display */}
            {selectedAvatar ? (
                <div className="avatar-display" dangerouslySetInnerHTML={{ __html: selectedAvatar }} />
            ) : (
                <p>{language === "en" ? "No avatar selected." : "ŸÑŸÖ Ÿäÿ™ŸÖ ÿßÿÆÿ™Ÿäÿßÿ± ÿµŸàÿ±ÿ© ÿ±ŸÖÿ≤Ÿäÿ©."}</p>
            )}

            {/* Celebration message if avatar is selected */}
            {selectedAvatar && (
                <div className="celebration-message">
                    {language === "en"
                        ? "Great choice! üéâ Your profile creation is completed! ü•≥"
                        : " ü•≥ ! ÿßÿÆÿ™Ÿäÿßÿ± ÿ±ÿßÿ¶ÿπ ! üéâ ŸÑŸÇÿØ ÿßŸÉÿ™ŸÖŸÑ ÿ•ŸÜÿ¥ÿßÿ° ŸÖŸÑŸÅŸÉ ÿßŸÑÿ¥ÿÆÿµŸä "}
                </div>
            )}

            {/* Continue to main page */}
            <button
                className="nextpage-continue-button"
                onClick={() => navigate("/main-page")}
            >
                {language === "en" ? "Continue" : "ÿßÿ≥ÿ™ŸÖÿ±"}
            </button>
        </div>
    );
}
